import React, { Component } from 'react'
import Link from "next/link";

class DropDownMenu extends Component {
  constructor() {
    super();

    this.state = {
      showMenu: false,
    }

    this.showMenu = this.showMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({
      showMenu: !this.state.showMenu,
    });
  }

  render() {
    return (
      <div style={{'cursor':'pointer'}}>
        <div className='nav-item nav-link' onClick={this.showMenu}>
          更多
        </div>

        {
          this.state.showMenu
            ? (
              <div className="menu">
                <Link href="/sports">
                  <a className='nav-item nav-link'>玩家評價</a>
                </Link>
                <Link href="/sports">
                  <a className='nav-item nav-link'>存取流程</a>
                </Link>
                <Link href="/sports">
                  <a className='nav-item nav-link'>APP下載</a>
                </Link>
                <Link href="/sports">
                  <a className='nav-item nav-link'>代理加盟</a>
                </Link>
                <Link href="/sports">
                  <a className='nav-item nav-link'>關於卡利</a>
                </Link>
              </div>
            )
            : null
        }

      </div>
    )
  }
}

export default DropDownMenu
