import React, { useState, useEffect } from 'react';
import css from '../styles/main.scss';
const customStyle = { cursor: 'pointer', position: 'relative' };
const customBtn = {
  backgroundColor: 'deepskyblue',
  color: 'white',
  borderRadius: 20
};
const walletData = [
  { name: 'DG百家樂', value: 0 },
  { name: 'DG百家樂', value: 0 },
  { name: 'DG百家樂', value: 0 },
  { name: 'DG百家樂', value: 0 },
  { name: 'DG百家樂', value: 0 }
];
const DDWallet = () => {
  const [showMenu, setShowMenu] = useState(false);
  const dropMenu = event => {
    setShowMenu(!showMenu);
  };
  const closeMenu = () => {
    setShowMenu(false);
  };

  useEffect(() => {
    window.addEventListener('click', closeMenu);
    return window.removeEventListener('click', closeMenu);
  });
  const _class = {
    true: 'iconfont icon_chevron-up-light',
    false: 'iconfont icon_chevron-down-light'
  };
  return (
    <div style={customStyle}>
      <span onClick={dropMenu} className={_class[showMenu]} />
      {showMenu ? (
        <div className={css.DDWallet}>
          <div className={css.itemsBox}>
            <div className={css.item}>
              <div className={css.itemName}>DG百家樂</div>
              <div className={css.itemValue}>0</div>
            </div>
          </div>
          <div className={css.bottomBox}>
            <div className={css.sumBox}>
              <div>總額度</div>
              <div>250</div>
            </div>
            <button className="btn" style={customBtn}>
              全部轉回主帳戶
            </button>
          </div>
        </div>
      ) : (
        ''
      )}
    </div>
  );
};

export default DDWallet;
