import React, {Fragment, useState} from 'react';
import css from '../styles/main.scss'
import {createMuiTheme, ThemeProvider, makeStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Modal from "react-modal";
import OutlineBtn from "./OutlineBtn";

const modalStyles = {
  content: {
    maxWidth: '800px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    padding: '0',
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none'
  }
};
Modal.setAppElement('#__next');

function InfoModify(props) {
  const [modalIsOpen, setModalIsOpen] = useState(true);
  const [passModalIsOpen, setPassModalIsOpen] = useState(false);
  const openModal = () => {
    setModalIsOpen(true);
    setPassModalIsOpen(true)
  };


  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.subtitle.style.color = 'white';
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);

  };


  const closePassModal = () => {
    setPassModalIsOpen(false)

  };

  return (
    <Fragment>
      <button
        onClick={openModal}
        style={{marginRight: '5px'}}
        type="button"
        className="btn btn-dark btn-sm"
      >
        立即註冊&nbsp;
        <span
          className="iconfont icon_user-plus-light"
          style={{color: 'white'}}
        />
      </button>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="wallet"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            加入會員
          </div>
          <span className="iconfont icon_times-light" onClick={closeModal}/>
        </div>

        <div className={`${css.modalBody}`}>
          <form style={{padding: '0 20px'}}>
            <div className={css.title}>變更暱稱與密碼</div>
            <div className="form-group">
              <label>暱 稱</label>
              <input
                type="text"
                className="form-control"
                placeholder="6-10位英、數字符"
              />
            </div>
            <div className="form-group">
              <label>新帳號密碼</label>
              <input
                type="password"
                className="form-control"
                placeholder="6-10位英、數字符"
              />
            </div>
            <div className="form-group">
              <label>確認新帳號密碼</label>
              <input
                type="password"
                className="form-control"
                placeholder="6-10位英、數字符"
              />
            </div>
            <div className="form-group">
              <label>新提款密碼</label>
              <input
                type="password"
                className="form-control"
                placeholder="6-10位英、數字符"
              />
            </div>
            <div className="form-group">
              <label>確認新提款密碼</label>
              <input
                type="password"
                className="form-control"
                placeholder="6-10位英、數字符"
              />
            </div>
            <div className="form-group form-check">
              <input
                type="checkbox"
                className="form-check-input"
                id="exampleCheck1"
              />
              <label className="form-check-label" htmlFor="exampleCheck1">
                是否使用提款密碼
              </label>
            </div>

            <div className="form-group form-check">
              <input
                type="checkbox"
                className="form-check-input"
                id="exampleCheck2"
              />
              <label className="form-check-label" htmlFor="exampleCheck2">
                是否通過手機接收優惠訊息
              </label>
            </div>

            <div className="form-group form-check">
              <input
                type="checkbox"
                className="form-check-input"
                id="exampleCheck3"
              />
              <label className="form-check-label" htmlFor="exampleCheck3">
                接收存、提款完成通知
              </label>
            </div>

          </form>

        </div>
        <OutlineBtn name="確認修改" style={{margin: '0 auto 30px', width: '80%'}}/>


      </Modal>

      <Modal
        isOpen={passModalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closePassModal}
        style={modalStyles}
        contentLabel="enterPassWords"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            請輸入提款密碼
          </div>
          <span className="iconfont icon_times-light" onClick={closePassModal}/>
        </div>
        <div className={css.modalBody}>
          <form className={css.formGroup}>
            <div className="inputWithBtn">
              <input type="text" placeholder="6-10位英、數字符"/>
              <button type="button" className="btn btn-primary btn-sm">
                忘記密碼
              </button>
            </div>
            <style jsx>
              {`
                .inputWithBtn {
                  display: flex;
                  margin-top: 20px;
                  margin-bottom: 10px;
                }
                .inputWithBtn .btn {
                  flex: 1 1 40%;
                  margin-left: 10px;
                }
                .btn-submit {
                  background-color: #32c5ff;
                  color: white;
                }
              `}
            </style>
            <button type="button" className="btn btn-submit btn-sm">
              確認送出
            </button>
          </form>
        </div>
      </Modal>
    </Fragment>
  );
}

export default InfoModify;
