import React, {Fragment} from 'react'
import SectionTitle from "./includes/SectionTitle";
import Card from "./Card";

const cardDataSet = [
  {
    'pic': 'images/about/img-about1.png',
    'title': '誠信與服務'
  },
  {
    'pic': 'images/about/img-about2.png',
    'title': '付款'
  },
  {
    'pic': 'images/about/img-about3.png',
    'title': '責任博彩'
  },
  {
    'pic': 'images/about/img-about4.png',
    'title': '嚴格保密及安全性維護'
  }
]

const Footer = () => (
  <Fragment>
    <div className='footer'>
      <div className="container">
        <SectionTitle subject='卡利合作娛樂品牌' textShadow={false}/>
        <div className="row">
          <div className="iconBox">
            <span className='iconfont icon_allbet' style={{'fontSize':'7rem'}}/>
            <span className='iconfont icon_DreamGaming'/>
            <span className='iconfont icon_WM' style={{'fontSize':'4rem'}}/>
            <span className='iconfont icon_SAgaming' style={{'fontSize':'5rem'}}/>
            <span className='iconfont icon_SUPER'/>
            <span className='iconfont icon_AFBCash' style={{'fontSize':'4.5rem'}}/>
          </div>
          <div className="iconBox">
            <span className='iconfont icon_Klottery'/>
            <span className='iconfont icon_SUPERbingo' style={{'fontSize':'5rem'}}/>
            <span className='iconfont icon_RTGslots' style={{'fontSize':'4.8rem'}}/>
            <span className='iconfont icon_betixion'/>
            <span className='iconfont icon_betsoft'/>
          </div>
        </div>

      </div>
    </div>
    <style jsx>{
      `
          .iconBox:first-child {
            margin-top: -20px;
          }
          .iconBox {
            margin: 0 auto;
            max-width: 800px;
            display:flex;
            justify-content: center;
            align-items: center;
            flex-wrap: wrap;
            max-height: 120px;
          }
          span.iconfont {
            font-size: 6rem;
            margin: 0 20px;
            background-image: linear-gradient(0deg, #6236FF 0%, #9a5fc9 100%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;

          }
          span.iconfont:hover {
            color: #ce70ff;
            text-shadow: 0 1px 3px rgba(0,0,0,.65);
            background-image: unset;
            -webkit-background-clip: unset;
            -webkit-text-fill-color: unset;
          }
          p {
          font-weight:300;
          letter-spacing: 1px;
          line-height: 1.8;
          max-width: 620px;
          margin: 0 auto 38px;
          color: #fff;
          text-align: center;
          text-shadow: 0 2px 4px #000000;

          }
          .flex {
            width: 100%;
            display: flex;
            justify-content: space-around;
          }
          .subTitle{
            color: #fff;
            font-size: 2.125rem;
            margin: -10px auto 20px;
            text-align: center;
            letter-spacing: 0;
            text-shadow: 0 2px 4px #000000;
          }
          .footer::after {
          content: "";
          background: url(images/bg_section_cooperation.png) no-repeat;
          opacity: 1;
          top: 0;
          left: 0;
          bottom: 0;
          right: 0;
          position: absolute;
          z-index: -1;  
          }
          .footer {
          padding: 0 0 78px;  
          position: relative;
          z-index: 0;
          background: unset;
          }`
    }

    </style>
  </Fragment>
);

export default Footer
