import React, { Fragment, Component, useRef, useEffect,useState } from 'react';
import css from '../styles/main.scss';
import aboutData from '../data/about';


const SwitchTabs = () => {
const [tabsActiveDefault, setTabsActiveDefault] = useState([true, false, false, false])
const expandTab=(index)=> {
    // console.log('open tab!!', index);
    const reset = this.state.TabsActiveDefault.map(() => false);
    reset[index] = true;
    this.setState({
      TabsActiveDefault: reset
    });
  }

  const renderAbouts=(data)=> {
    const style = {
      true: { display: 'flex' },
      false: { display: 'none' }
    };
    return data.map((d, i) => (
      <div
        style={style[this.state.TabsActiveDefault[i]]}
        key={d.index}
        className={css.itemBox}
      >
        <div className={css.itemColumn}>
          <img src={d.tabImage} alt="" className="img-responsive" />
        </div>
        <div className={css.itemColumn}>
          <div className={css.title}>{d.tabTitle}</div>
          <p>{d.tabContent}</p>
        </div>
      </div>
    ));
  }
  const _class = {
    true: `${css.tab} ${css.active}`,
    false: `${css.tab}`
  };

  return (
  <Fragment>
        <div className={css.tabsBox}>
          <div
            onClick={() => this.expandTab(0)}
            className={_class[tabsActiveDefault[0]]}
          >
            <span className="iconfont icon_handshake-solid" />
            <div>誠信與服務</div>
          </div>
          <div
            onClick={() => this.expandTab(1)}
            className={_class[this.state.TabsActiveDefault[1]]}
          >
            <span className="iconfont icon_payment" />
            <div>付款</div>
          </div>
          <div
            onClick={() => this.expandTab(2)}
            className={_class[this.state.TabsActiveDefault[2]]}
          >
            <span className="iconfont icon_responsibility" />
            <div>責任博彩</div>
          </div>
          <div
            onClick={() => this.expandTab(3)}
            className={_class[this.state.TabsActiveDefault[3]]}
          >
            <span className="iconfont icon_security" />
            <div>
              嚴格保密及
              <br />
              安全性維護
            </div>
          </div>
        </div>
        {this.renderAbouts(aboutData)}
      </Fragment>
      )
}
