import React, { Component, Fragment } from 'react';
import DeskTopLayout from '../../components/layouts/deskTopLayout';
import withSizes from 'react-sizes';
import css from '../../styles/main.scss';
import OutlineBtn from '../../components/OutlineBtn';
import Footer from '../../components/Footer';
import MobileLayout from '../../components/layouts/MobileLayout';
import StaticBanner from '../../components/includes/StaticBanner';
import Bonus from '../../components/Bonus';
import bonus from '../../data/bonus';
import Link from 'next/link';

const banner = {
  title: '優惠活動',
  desc:
    '卡利娛樂城致力為廣大玩家提供優質頂尖的網上博彩平臺，業務服務全力以赴，開立賬戶完全免費，存款5分鐘、取款10分鐘迅速完成。',
  pic: '/images/background/bonus.png'
};

class Post extends Component {
  static getInitialProps({ query }) {
    // console.log('query:', post);
    // console.log(bonus);
    let post = bonus[query.postId - 1];
    return { post };
  }
  showPost = post => (
    <div className={css.bonusBox}>
      <img src={post.pic} alt="" className="img-responsive" />
      <div>{post.title}</div>
      <div>{post.context}</div>
      <OutlineBtn name='返回'/>
    </div>
  );

  render() {
    const { post } = this.props;
    return (
      <Fragment>
        <DeskTopLayout>
          <div className="container">
            <StaticBanner
              title={banner.title}
              desc={banner.desc}
              bannerIMG={banner.pic}
            />
            {this.showPost(post)}
          </div>

          <Footer />
        </DeskTopLayout>
        <div className={css.displayMobile}>
          <MobileLayout></MobileLayout>
        </div>
      </Fragment>
    );
  }
}
