import React, { Component, Fragment } from 'react';
import DeskTopLayout from '../../components/layouts/deskTopLayout';
import withSizes from 'react-sizes';
import css from '../../styles/main.scss';
import OutlineBtn from '../../components/OutlineBtn';
import Footer from '../../components/Footer';
import MobileLayout from '../../components/layouts/MobileLayout';
import StaticBanner from '../../components/includes/StaticBanner';
import Bonus from '../../components/Bonus';

const banner = {
  title: '關於卡利',
  desc:
    '卡利娛樂城 (官方直營網址: cali99.net) 為卡利集團專營的線上博奕平台，我們秉持誠信美譽、效率快速的宗旨，為會員提供優良完善的遊戲環境。',
  pic: 'images/background/about.png'
};


const About = ({ isMobile }) => {
  return (
    <Fragment>
      <DeskTopLayout>
        <div className="container">
          <StaticBanner
            title={banner.title}
            desc={banner.desc}
            bannerIMG={banner.pic}
          />
          <div
            className={css.banner}
            style={{ backgroundImage: `url(${bannerIMG})` }}
          >
            <div className={css.title}>亞洲最頂級的線上博彩娛樂平台</div>
            <div className={css.desc}>
              卡利集團（Calibet），以研發優質線上博奕平臺為宗旨。隨著近年來網路博彩的風潮而起，在高超網路技術團隊之下，將數十位博彩專家的精隨發揮得淋漓盡致，成功打造出一個以真人百家樂遊戲為主的頂級博彩平臺。
            </div>
          </div>
        </div>

        <Footer />
      </DeskTopLayout>
      {isMobile && (
        <MobileLayout>
          <div className={css.mobileEmpty} />
          <div
            className={css.gameCardGroup__mobile}
            style={{ height: '650px' }}
          >
            <div
              className={css.gameCard}
              style={{
                backgroundImage:
                  'linear-gradient(90deg, rgba(0,0,0,0.00) 40%, rgba(0,0,0,0.75) 100%),url(images/beauty/fish01.png),url(images/background/fish01.png)',
                backgroundSize: 'cover, 55%, cover',
                backgroundPosition: 'center, left top, center'
              }}
            >
              <OutlineBtn />
            </div>
            <div
              className={css.gameCard}
              style={{
                backgroundImage:
                  'url(images/beauty/fish02.png),linear-gradient(90deg, rgba(0,0,0,0.00) 40%, rgba(0,0,0,0.75) 100%),url(images/background/fish02.png)',
                backgroundSize: ' 73%,cover, cover',
                backgroundPosition: 'left top,center,  center'
              }}
            >
              <OutlineBtn />
            </div>
          </div>
        </MobileLayout>
      )}
    </Fragment>
  );
};

const mapSizesToProps = ({ width }) => ({
  isTablet: width >= 480 && width < 1024,
  isDesktop: width >= 1024,
  under768: width <= 768,
  isMobile: width <= 480
});
export default withSizes(mapSizesToProps)(About);
