  constructor(props) {
    super(props);
    // console.log(props);

    this.state = {
      users: props.userData
    };
  }

  render() {
    return (
      <Fragment>
        <div className="headerBG">
          <div className="flexContainer">
            <button
              style={{ marginRight: '5px' }}
              type="button"
              className="btn btn-dark btn-sm"
            >
              立即註冊&nbsp;
              <span
                className="iconfont icon_user-plus-light"
                style={{ color: 'white' }}
              />
            </button>
            <form className={css.flex}>
              <input
                style={{ marginRight: '5px' }}
                type="text"
                className={css.input}
                placeholder="會員賬號"
              />
              <input
                style={{ marginRight: '5px' }}
                type="text"
                className={css.input}
                placeholder="登入密碼"
              />
            </form>
            <button
              style={{ marginRight: '5px' }}
              type="button"
              className="btn btn-outline-dark btn-sm"
            >
              登入
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm">
              忘記密碼
            </button>
          </div>

        </div>
      </Fragment>
    );
  }
}

export default Header;
