import React from 'react';
import css from '../styles/main.scss';
import Router from 'next/router';

const OutlineBtn = ({ path, func, back, name }) => {
  const handleClick = () => {
    if (path) {
      () => Router.push(path);
    } else if (func) {
      func;
    } else if (back) {
      // console.log('back!!');
      () => Router.back();
    }
  };
  return (
    <div onClick={handleClick} className={css.outlineBtn}>
      {name}
    </div>
  );
};

export default OutlineBtn;
