import React, {Fragment, useState} from 'react';
import Modal from "react-modal";
import css from "../styles/main.scss";

const modalStyles = {
  content: {
    width: '320px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#d8d8d8',
    padding: '0',
    top: '35%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none'
  }
};
Modal.setAppElement('#__next');

const WithdrawPassModal = ({open}) => {

  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  if(open){
    setModalIsOpen(true);
  }

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.subtitle.style.color = 'white';
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  return (<Modal
    isOpen={modalIsOpen}
    onAfterOpen={afterOpenModal}
    onRequestClose={closeModal}
    style={modalStyles}
    contentLabel="enterPassWords"
  >
    {open && <Fragment>
      <div className={css.modalHeader}>
        <div ref={subtitle => (Modal.subtitle = subtitle)}>
          請輸入提款密碼
        </div>
        <span className="iconfont icon_times-light" onClick={closeModal}/>
      </div>
      <div className={css.modalBody}>
        <form className={css.formGroup}>
          <div className="inputWithBtn">
            <input type="text" placeholder="6-10位英、數字符"/>
            <button type="button" className="btn btn-primary btn-sm">
              忘記密碼
            </button>
          </div>
          <style jsx>
            {`
                .inputWithBtn {
                  display: flex;
                  margin-top: 20px;
                  margin-bottom: 10px;
                }
                .inputWithBtn .btn {
                  flex: 1 1 40%;
                  margin-left: 10px;
                }
                .btn-submit {
                  background-color: #32c5ff;
                  color: white;
                }
              `}
          </style>
          <button type="button" className="btn btn-submit btn-sm">
            確認送出
          </button>
        </form>
      </div>
    </Fragment>}
  </Modal>);
}

export default WithdrawPassModal;
