import React, { Component, Fragment, useState } from 'react';
import Link from 'next/link';
import css from '../../styles/main.scss';
import Router from 'next/router';
import Measure from 'react-measure';
import UserStateBar from '../includes/UserStateBar';

const MainNavMobileLevel2 = props => {
  const hamburger = {
    marginLeft: '10px',
    fontSize: '1.625rem'
  };
  const [dimensions, setDimensions] = useState({
    width: -1,
    height: -1
  });
  const { width, height } = dimensions;
  const [isUser, setIsUser] = useState(true);

  return (
    <Fragment>
    <Measure
        bounds
        onResize={contentRect => {
          setDimensions(contentRect.bounds);
        }}
      ></Measure>
      <div ref={measureRef} className={`fixed-top`} height={getHeight(height)}>
        <nav className={`navbar ${css.navBarFlex}`}>
          <span
            onClick={() => Router.back()}
            className="iconfont icon_chevron-left-light"
            style={hamburger}
          />
          <div className={css.title}>{props.title}</div>
        </nav>
      </div>
    </Fragment>
  );
};

export default MainNavMobileLevel2;
