import React, { useState } from 'react';
import css from '../styles/main.scss';

const Item = ({ from, time }) => {
  const _class = {
    true: 'iconfont icon_chevron-up-light',
    false: 'iconfont icon_chevron-down-light'
  };

  const [accordian, setAccordian] = useState(false);
  const showAccordian = () => {
    setAccordian(!accordian);
  };

  return (
    <div className={css.itemContainer}>
      <div
        onClick={showAccordian}
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          width: '100%',
          height: 50
        }}
      >
        <div
          style={{
            display: 'flex',
            alignItems: 'center'
          }}
        >
          <div style={{ fontSize: '1rem', color: 'black' }}>{from}</div>
        </div>
        <div style={{ fontSize: '1rem', color: '#6D7278' }}>{time}</div>
        <span
          className={_class[accordian]}
          style={{ fontSize: '1rem', color: '#9BA2AA' }}
        />
      </div>
      {accordian && (
        <div
          style={{
            width: '100%',
            padding: '20px 0',
            borderTop: '1px solid #979797'
          }}
        ></div>
      )}
    </div>
  );
};

export default Item;
