import React, { useState, useEffect } from 'react';
import css from '../styles/main.scss';
const customStyle = { cursor: 'pointer', position: 'relative' };
const customBtn = {
  backgroundColor: 'deepskyblue',
  color: 'white',
  borderRadius: 20
};
const DDWallet = () => {
  const [showMenu, setShowMenu] = useState(false);
  const dropMenu = event => {
    setShowMenu(!showMenu);
  };
  const closeMenu = () => {
    setShowMenu(false);
  };

  useEffect(() => {
    window.addEventListener('click', closeMenu);
    return window.removeEventListener('click', closeMenu);
  });
  return (
    <div style={customStyle}>
      <span onClick={dropMenu} className="iconfont icon_chevron-down-light" />
      {showMenu ? (
        <div className={css.DDWallet}>
          <div className={css.itemsBox}></div>
          <div className={css.bottomBox}>
            <div>
              <div>總額度</div>
              <div>2</div>
            </div>
            <button className="btn" style={customBtn}>
              全部轉回主帳戶
            </button>
          </div>
        </div>
      ) : (
        ''
      )}
    </div>
  );
};

export default DDWallet;
