import React, {Component, Fragment} from 'react';
import withSizes from 'react-sizes';
import axios from 'axios';
import DeskTopLayout from '../components/layouts/deskTopLayout';
import MobileLayout from '../components/layouts/MobileLayout';
import StartPlayingDesktop from '../components/StartPlayingDesktop';
import UserEvaluationSec from '../components/UserEvaluationSec';
import Bonus from '../components/Bonus';
import AboutSec from '../components/AboutSec';
import Footer from '../components/Footer';
import Slider from 'react-slick';
import SectionTitle from '../components/includes/SectionTitle';
import css from '../styles/main.scss';
import Link from 'next/link';

const bonus = [
  {
    pic: 'images/bonus/img-promotion-2.png',
    title: '賭神賽',
    context:
      '感謝廣大會員持續遊玩任你博網站，為回饋熱情支持，推出一個月一次的會員活動會員日的每筆存款都可申請儲值回饋金，使用指定收款帳戶入款，回饋金更高達15%呢！還不快點通知親朋好友一起共襄盛舉!!!',
    time: '2020.2.10.Mon'
  },
  {
    pic: 'images/bonus/img-promotion-3.png',
    title: '賀聖誕派大獎 贏返水迎元旦',
    context:
      '感謝廣大會員持續遊玩任你博網站，為回饋熱情支持，推出一個月一次的會員活動會員日的每筆存款都可申請儲值回饋金，使用指定收款帳戶入款，回饋金更高達15%呢！還不快點通知親朋好友一起共襄盛舉!!!',
    time: '2020.2.10.Mon'
  },
  {
    pic: 'images/bonus/img-promotion-4.png',
    title: '卡利系統一律返水1%',
    context:
      '感謝廣大會員持續遊玩任你博網站，為回饋熱情支持，推出一個月一次的會員活動會員日的每筆存款都可申請儲值回饋金，使用指定收款帳戶入款，回饋金更高達15%呢！還不快點通知親朋好友一起共襄盛舉!!!',
    time: '2020.2.10.Mon'
  },
  {
    pic: 'images/bonus/img-promotion-5.png',
    title: '賀聖誕 派大獎',
    context:
      '感謝廣大會員持續遊玩任你博網站，為回饋熱情支持，推出一個月一次的會員活動會員日的每筆存款都可申請儲值回饋金，使用指定收款帳戶入款，回饋金更高達15%呢！還不快點通知親朋好友一起共襄盛舉!!!',
    time: '2020.2.10.Mon'
  }
]
const settings = {
  autoplay: true,
  dots: true,
  infinite: true,
  arrows: true,
  fade: true,
  speed: 3000,
  slidesToShow: 1,
  slidesToScroll: 1
};
const settings_bonus = {
  infinite: true,
  slidesToShow: 3,
  slidesToScroll: 3,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    },
    {
      breakpoint: 580,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1
      }
    }
  ]
};

const Index = ({isExcludeMobile,isMobile}) => {
  return (
    <Fragment>
      {isExcludeMobile?<DeskTopLayout>
        <Slider {...settings}>
          <img
            src="images/banner/banner01.jpeg"
            alt=""
            className="img-responsive"
          />
          <img
            src="images/banner/banner02.jpg"
            alt=""
            className="img-responsive"
          />
          <img
            src="images/banner/banner03.jpg"
            alt=""
            className="img-responsive"
          />
          <img
            src="images/banner/banner04.jpg"
            alt=""
            className="img-responsive"
          />
        </Slider>
        <div className="container">
          <SectionTitle subject="開始遊玩"/>

          <StartPlayingDesktop/>

          <SectionTitle subject="優惠活動"/>

          <Slider {...settings_bonus}>
            {bonus.map((b, i) => (
              <Bonus
                key={i}
                pic={b.pic}
                title={b.title}
                time={b.time}
                context={b.context}
              />
            ))}
          </Slider>
        </div>
        <UserEvaluationSec/>
        <AboutSec/>
        <Footer/>
      </DeskTopLayout>:
        <MobileLayout>
          <h1>Hello from Mobile!!</h1>
        </MobileLayout>}

    </Fragment>
  );
}


const mapSizesToProps = ({width}) => ({
  isExcludeMobile: width >= 480 && width < 3000,
  isMobile: width < 480
});
export default withSizes(mapSizesToProps)(Index);
