import React, {Component, Fragment} from 'react'
import Link from "next/link";
import DropDownMenu from '../DDM'
import css from "../../styles/main.scss";
import withSizes from 'react-sizes';


const MainNav = ({isMobile, isTablet}) => (
  <>
    <header>
      <div className="container">
        <nav className="navbar navbar-expand-lg navbar-light">
          {isTablet?<span
            className='iconfont icon_bars-light'
          />:''}
          <Link href='/'>
            <a className={`navbar-brand ${css.logo}`}>
              <img className='img-responsive logo' src="images/Cali_logo_02.png" alt=""/>
            </a>
          </Link>
          {isTablet ? '' : <div className="collapse navbar-collapse">
            <div className={css.flexContainer}>
              <Link href="/online_baccarat">
                <a className='nav-item nav-link'>真人娛樂</a>
              </Link>

              <Link href="/sports">
                <a className='nav-item nav-link'>體育博彩</a>
              </Link>
              <Link href="/lottery">
                <a className='nav-item nav-link'>賓果彩票</a>
              </Link>
              <Link href="/electronics">
                <a className='nav-item nav-link'>電子遊戲</a>
              </Link>
              <Link href="/fish">
                <a className='nav-item nav-link'>捕魚王</a>
              </Link>
              <Link href="/promotions">
                <a className='nav-item nav-link'>優惠活動</a>
              </Link>
              <DropDownMenu/>
            </div>

          </div>}
        </nav>
      </div>
      <style jsx>
        {
          `
          span.iconfont {
          margin-left: 10px;
            font-size: 1.875rem;
            background: linear-gradient(
              180deg,
              #fdf8a2 3%,
              #f4d97a 47%,
              #eec462 100%
            );
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent; 
          }
          `
        }
      </style>
    </header>
  </>
);

const mapSizesToProps = ({width}) => ({
  isMobile: width < 480,
  isTablet: width >= 480 && width < 1024
});
export default withSizes(mapSizesToProps)(MainNav);
