import React, {Component, Fragment} from 'react'
import Link from "next/link";
import css from "../../styles/main.scss";
import {makeStyles} from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';


class MainNavMobile extends Component {
  render() {
    const useStyles = makeStyles({
      list: {
        width: 250,
      },
      fullList: {
        width: 'auto',
      },
    });
    const classes = useStyles();
    return (
      <Fragment>
        <header>
          <nav className={`navbar fixed-top ${css.navBarFlex}`}>
            <span className='iconfont icon_bars-light'/>
            <Link href='/'>
              <a className={css.logo}>
                <img className='img-responsive logo_m' src="images/Cali_logo_mobile.png" alt=""/>
              </a>
            </Link>
            <div className={css.btnGroup}>
              <button type="button" className={css.btnOutLine}>註冊</button>
              <button type="button" className={css.btnOutLine}>登入</button>
            </div>
          </nav>
          <style jsx>
            {
              `
          span.iconfont {
          margin-left: 10px;
            font-size: 1.625rem;
            background: linear-gradient(
              180deg,
              #fdf8a2 3%,
              #f4d97a 47%,
              #eec462 100%
            );
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent; 
          }
          `
            }
          </style>
        </header>
      </Fragment>
    );
  }
}

export default MainNavMobile
