import React, { Component, Fragment } from 'react';
import DeskTopLayout from '../../components/layouts/deskTopLayout';
import MobileLayout from '../../components/layouts/MobileLayout';
import SectionTitle from '../../components/includes/SectionTitle';
import Header from '../../components/includes/Header';
import withSizes from 'react-sizes';
import css from '../../styles/main.scss';

const Baccarat = ({ isMobile, isTablet, isDesktop }) => {
  return (
    <Fragment>
      <DeskTopLayout>
        <div className="container">
          <div
            className={css.gameCard}
            style={{
              backgroundImage:
                'linear-gradient(180deg, rgba(0,0,0,0.00) 50%, #000000 95%),url(images/beauty/baccarat01.png),url(images/background/baccarat01.png)'
            }}
          ></div>
        </div>
      </DeskTopLayout>
      {isMobile && (
        <MobileLayout>
          <div className={css.mobileEmpty} />
          <h1>mobile layout</h1>
        </MobileLayout>
      )}
    </Fragment>
  );
};

const mapSizesToProps = ({ width }) => ({
  isTablet: width >= 480 && width < 1024,
  isDesktop: width >= 1024,
  isMobile: width <= 480
});
export default withSizes(mapSizesToProps)(Baccarat);
