import React, {Fragment, useState} from 'react';
import css from '../styles/main.scss'
import {createMuiTheme, ThemeProvider, makeStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Modal from "react-modal";
import OutlineBtn from "./OutlineBtn";

const modalStyles = {
  content: {
    width: '100%',
    maxWidth: '500px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    padding: '0',
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none'
  }
};
Modal.setAppElement('#__next');

function InfoModify(props) {
  const [modalIsOpen, setModalIsOpen] = useState(true);
  const [passModalIsOpen, setPassModalIsOpen] = useState(false);
  const openModal = () => {
    setModalIsOpen(true);
    setPassModalIsOpen(true)
  };


  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.subtitle.style.color = 'white';
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);

  };


  const closePassModal = () => {
    setPassModalIsOpen(false)

  };

  return (
    <Fragment>
      <button
        onClick={openModal}
        style={{marginRight: '5px'}}
        type="button"
        className="btn btn-dark btn-sm"
      >
        立即註冊&nbsp;
        <span
          className="iconfont icon_user-plus-light"
          style={{color: 'white'}}
        />
      </button>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="wallet"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            加入會員
          </div>
          <span className="iconfont icon_times-light" onClick={closeModal}/>
        </div>

        <div className={`${css.modalBody}`}>
          <form>
            <div className="form-group">
              <input
                style={{ marginBottom: '20px' }}
                placeholder="手機驗證"
                type="email"
                className="form-control"
                id="mobileMessageVailidation"
                aria-describedby="emailHelp"
              />
              <label htmlFor="exampleInputEmail1">手機號碼</label>
              <div className={css.inputGroup}>
                <input
                  type="email"
                  className="form-control"
                  id="exampleInputEmail1"
                  aria-describedby="emailHelp"
                />
                <button type="button" className="btn btn-outline-light">
                  簡訊
                </button>
              </div>
            </div>
            <div className="form-group" style={{ marginBottom: '30px' }}>
              <label htmlFor="exampleInputPassword1">驗證碼</label>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
              />
            </div>

            <OutlineBtn path="/register/second-step" name="確認送出" />
          </form>

        </div>


      </Modal>

      <Modal
        isOpen={passModalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closePassModal}
        style={modalStyles}
        contentLabel="enterPassWords"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            請輸入提款密碼
          </div>
          <span className="iconfont icon_times-light" onClick={closePassModal}/>
        </div>
        <div className={css.modalBody}>
          <form className={css.formGroup}>
            <div className="inputWithBtn">
              <input type="text" placeholder="6-10位英、數字符"/>
              <button type="button" className="btn btn-primary btn-sm">
                忘記密碼
              </button>
            </div>
            <style jsx>
              {`
                .inputWithBtn {
                  display: flex;
                  margin-top: 20px;
                  margin-bottom: 10px;
                }
                .inputWithBtn .btn {
                  flex: 1 1 40%;
                  margin-left: 10px;
                }
                .btn-submit {
                  background-color: #32c5ff;
                  color: white;
                }
              `}
            </style>
            <button type="button" className="btn btn-submit btn-sm">
              確認送出
            </button>
          </form>
        </div>
      </Modal>
    </Fragment>
  );
}

export default InfoModify;
