import React from 'react'

class ScrollButton extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      intervalId: 0,
      goTopShow: false
    };
  }

  componentDidMount() {
    window.addEventListener('scroll', function(e){
      if(document.documentElement.scrollTop>120){
        // this.setState({
        //   goTopShow:true
        // });
        this.setState({ goTopShow:true });
        console.log('over 120');
      }else {
        // this.setState({
        //   goTopShow:false
        // });
        this.setState({ goTopShow:false });
        console.log('less than 120');
      }
    })
  }

  scrollStep() {
    if (window.pageYOffset === 0) {
      clearInterval(this.state.intervalId);
    }
    window.scroll(0, window.pageYOffset - this.props.scrollStepInPx);
  }

  scrollToTop() {
    let intervalId = setInterval(this.scrollStep.bind(this), this.props.delayInMs);
    this.setState({ intervalId: intervalId });
  }

  render () {
    return <button title='Back to top' className={this.state.goTopShow?'scroll active':'scroll'}
                   onClick={ () => { this.scrollToTop(); }}>
      <span className='arrow-up iconfont iconchevron-up-solid'/>
    </button>;
  }
}

export default ScrollButton
