import React from 'react';
import css from '../styles/main.scss';
import Router from 'next/router';

const OutlineBtn = ({ path, func, back, name }) => {
  const handleClick = () => {
    if (path) {
      return Router.push(path);
    } else if (func) {
      func();
    } else if (back) {
      return Router.back();
    }
  };
  return (
    <div onClick={handleClick} className={css.outlineBtn}>
      {name}
    </div>
  );
};

export default OutlineBtn;
