import React, {Fragment, useState} from 'react';
import css from '../../styles/main.scss';
import Router from 'next/router';
import DDWallet from "../DDWallet";
import Wallet from "../Wallet";
import Modal from "react-modal";
import ReactTooltip from 'react-tooltip'

const modalStyles = {
  content: {
    position: 'relative',
    backgroundImage: 'linear-gradient(225deg, #32C5FF 0%, #B620E0 51%, #F7B500 100%)',
    maxWidth: '994px',
    maxHeight: '574px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
    backgroundColor: '#d8d8d8',
    padding: '0',
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none',
    overflow: 'unset'
  }
};

Modal.setAppElement('#__next');

const StatefulHeader = (props) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <Fragment>
      <div className={css.headerBG}>
        <div className={`${css.flexContainer} ${css.flexContainer__status}`}>

          <div className={css.userLevel} style={{color: '#F70000'}}>
            <span className="iconfont icon_star-solid" style={{fontSize: '1.2rem'}}/>
            <span className="iconfont icon_star-solid" style={{fontSize: '1.2rem'}}/>
            <span className="iconfont icon_star-solid" style={{fontSize: '1.2rem'}}/>
            <span className="iconfont icon_star-light" style={{fontSize: '1.2rem'}}/>
            <span className="iconfont icon_star-light" style={{fontSize: '1.2rem'}}/>
            <span className="iconfont icon_star-light" style={{fontSize: '1.2rem'}}/>
          </div>
          <div className={css.userInfo} style={{marginLeft: 20}}>歡迎test123</div>
          <span className="iconfont icon_usd-circle-light" style={{marginLeft: 20}}/>
          <div className={css.userInfo} style={{marginLeft: 20}}>250.00</div>
          <DDWallet/>
          <span onClick={openModal} data-tip="電子錢包" className="iconfont icon_wallet-light" style={{marginLeft: 20}}/>
          <ReactTooltip place='bottom' effect='solid'/>
          <span className="iconfont icon_user-alt-light" style={{marginLeft: 20}}/>
          <span
            onClick={() => Router.push('/notice')}
            className="iconfont icon_bell-light" style={{marginLeft: 20}}/>
          <span className="iconfont icon_sign-out-light" style={{marginLeft: 20}}/>

        </div>
      </div>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="Example Modal"
      >
        <div
          onClick={closeModal}
          className='wallet'>
          <span className="iconfont icon_times-light" style={{fontSize: 26, color: '#32C5FF'}}/>
        </div>
        <style jsx>
          {
            `
            .wallet {
              position: absolute;
              top: -18px;
              right: -18px;
              box-sizing: border-box;
              border: 6px solid #32C5FF;
              display: flex;
              justify-content: center;
              align-items: center;
              background-color: black;
              width: 50px;
              height: 50px;
              border-radius: 50px;
            }
            `
          }
        </style>
        <Wallet/>


      </Modal>

    </Fragment>
  );
}


export default StatefulHeader;
