import React, {Fragment, Component} from 'react'
import Measure from 'react-measure'
import Marquee from "react-smooth-marquee"
import Header from '../includes/Header'
import MainNav from '../includes/MainNavMobile'
import Head from 'next/head';
import css from "../../styles/main.scss"
import ScrollButton from "../ScrollButton";
import Drawer from "../Drawer";
import MainNavMobile from "../includes/MainNavMobile";
import BottomNav from "../includes/BottomNav";


class MobileLayout extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dimensions: {
        width: -1,
        height: -1,
      },
    };
  }
  render() {
    const {width, height} = this.state.dimensions;
    return (
      <Fragment>
        <Head>
          <link
            href="https://fonts.googleapis.com/css?family=Noto+Sans+TC:300,400,500,700&display=swap&subset=chinese-traditional"
            rel="stylesheet"/>
          <link rel="stylesheet"
                href="https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.4.1/css/bootstrap.min.css"/>
          <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"/>
          <link rel="stylesheet" type="text/css" charset="UTF-8"
                href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"/>
          <link rel="stylesheet" type="text/css"
                href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"/>
          <link rel="stylesheet" href="/css/styles.css"/>
          <link rel="stylesheet" href="/css/iconfont.css"/>
          <title>卡利娛樂城</title>
        </Head>
        <div className="mobileBody">
          <Measure
            bounds
            onResize={contentRect => {
              this.setState({dimensions: contentRect.bounds})
            }}
          >
            {({ measureRef }) => (<MainNavMobile ref={measureRef}/>)}

          </Measure>
          <div className='mainBgMobile'>
            {this.props.children}
          </div>
          <BottomNav />
          <style jsx>{
            `
          .mobileBody {
            height: 100%;
          }
          .mainBgMobile::after {
          content: "";
          background: url(images/stary_sky.png) repeat;
          background-size: contain;
          filter: contrast(100%);
          opacity: 0.32;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          position: absolute;
          z-index: -1;   
          }
          .mainBgMobile {
          height: calc(100%);
          overflow: scroll;
          position: relative;
          z-index: 0;
          background: linear-gradient(to right, #000000 0%, #6e41c4 50%, rgba(0,0,0,1) 100%);
          }`
          }

          </style>
        </div>

      </Fragment>
    );
  }
}

export default MobileLayout
