                style={{ marginRight: '5px' }}
                type="text"
                className={css.input}
                placeholder="登入密碼"
              />
            </form>
            <button
              style={{ marginRight: '5px' }}
              type="button"
              className="btn btn-outline-dark btn-sm"
            >
              登入
            </button>
            <button type="button" className="btn btn-outline-dark btn-sm">
              忘記密碼
            </button>
          </div>
        </div>
        <style jsx>
          {`
            .headerBG {

            }
            .flexContainer {
              height: 45px;
              display: flex;
              align-item: center;
              padding: 5px;
              justify-content: flex-end;
              max-width: 1100px;
              margin: 0 auto;

            }
          `}
        </style>
      </Fragment>
    );
  }
}

export default Header;
