import React, {Component, Fragment} from 'react';
import DeskTopLayout from '../layouts/deskTopLayout';
import css from '../../styles/main.scss';
import Router from 'next/router';
import DDWallet from "../DDWallet";

class Header extends Component {
  state = {
    email: '',
    password: ''
  };
  handleSubmit = event => {
    event.preventDefault();
    console.log(this.state);
  };

  render() {
    return (
      <Fragment>
        <div className={css.headerBG}>
          <div className={`${css.flexContainer} ${css.flexContainer__status}`}>

            <div className={css.userLevel} style={{color:'#F70000'}}>
              <span className="iconfont icon_star-solid"/>
              <span className="iconfont icon_star-solid"/>
              <span className="iconfont icon_star-solid"/>
              <span className="iconfont icon_star-light"/>
              <span className="iconfont icon_star-light"/>
              <span className="iconfont icon_star-light"/>
            </div>
            <div className={css.userInfo} style={{marginLeft:20}}>歡迎test123</div>
            <span className="iconfont icon_usd-circle-light" style={{marginLeft:20}}/>
            <div className={css.userInfo} style={{marginLeft:20}}>250.00</div>
            <DDWallet/>
            <span className="iconfont icon_transfer_dt" style={{marginRight:20}} />
            <span className="iconfont icon_deposit" style={{marginRight:20}} />
            <span className="iconfont icon_withdraw" style={{marginRight:20}} />
            <span className="iconfont icon_user-alt-light" style={{marginRight:20}} />
            <span
              onClick={() => Router.push('/notice')}
              className="iconfont icon_bell-light"/>
            <span className="iconfont icon_sign-out-light"/>

          </div>
        </div>
        <style jsx>
          {`
            @media (max-width: 1024px) {
              .flexContainer {
                justify-content: center !important;
              }
            }
            .headerBG {
              background-image: linear-gradient(
                180deg,
                #fdf8a2 3%,
                #f4d97a 47%,
                #eec462 100%
              );
            }
            .flexContainer {
              height: 45px;
              display: flex;
              align-item: center;
              padding: 5px;
              justify-content: flex-end;
              max-width: 1100px;
              margin: 0 auto;
            }
          `}
        </style>
      </Fragment>
    );
  }
}

export default Header;
