import React from 'react'
import Tabs from "./Tabs";

class StartPlayingDesktop extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      TabsActiveDefault: [true, false, false, false, false],
      TabsData: [
        {
          "index": "0",
          "icon": "baccarat",
          "tabName": "真人娛樂",
          "tanImage": "images/startPlay/truePeople_photo.jpg",
          "gameProvider": ["allbet", "DreamGaming", "WM", "SAgaming"]
        },
        {
          "index": "1",
          "icon": "futbol-solid",
          "tabName": "體育博彩",
          "tanImage": "images/startPlay/sport_photo.jpg",
          "gameProvider": ["SUPER", "AFBCash"]
        },
        {
          "index": "2",
          "icon": "ticket-alt-solid",
          "tabName": "賓果彩票",
          "tanImage": "images/startPlay/lottery_photo.jpg",
          "gameProvider": ["9Klottery", "SUPERbingo"]
        },
        {
          "index": "3",
          "icon": "alien-monster-solid",
          "tabName": "電子遊戲",
          "tanImage": "images/startPlay/poker_photo.jpg",
          "gameProvider": ["RTGslots", "SAgaming", "betixion", "betsoft"]
        },
        {
          "index": "4",
          "icon": "fish",
          "tabName": "捕魚王",
          "tanImage": "images/startPlay/fish_photo.jpg",
          "gameProvider": ["allbet", "SAgaming"]
        }
      ]
    }

    this.expandTab = this.expandTab.bind(this)
  }

  expandTab(index) {
    // console.log('open tab!!', index);
    const reset = this.state.TabsActiveDefault.map(() => false)
    reset[index] = true
    this.setState({
      TabsActiveDefault: reset
    });
  }

  render() {
    const {TabsActiveDefault, TabsData} = this.state;
    return (
      <ul>
        <Tabs active={TabsActiveDefault[TabsData]} open={this.expandTab} index={0} data={TabsData}/>
        <Tabs active={TabsActiveDefault[TabsData]} open={this.expandTab} index={1} data={TabsData}/>
        <Tabs active={TabsActiveDefault[TabsData]} open={this.expandTab} index={2} data={TabsData}/>
        <Tabs active={TabsActiveDefault[TabsData]} open={this.expandTab} index={3} data={TabsData}/>
        <Tabs active={TabsActiveDefault[TabsData]} open={this.expandTab} index={4} data={TabsData}/>
      </ul>
    )
  }
}

export default StartPlayingDesktop
