import React, {Component} from 'react';

class Drawer extends Component {
  render() {
    return (
      <div className='drawerBox'>
        <div className="infoBox">
          <p>在線客服</p>
          <img src="images/qrCode.png" alt="" className="img-responsive"/>
        </div>
        <div className="infoBox">
          <img src="images/line-messenger.svg" alt="" className="img-responsive"/>
          <p>@888pi</p>
        </div>
        <div className="infoBox">
          <p style={{'fontSize':'.85rem'}}>
            意見反饋
            8888pipipi@gmail.com
          </p>
        </div>
        <div className="TrapezoidTag">
          <span className='iconfont iconHR'/>
          <p>聯系我們</p>
        </div>
        <style jsx>
          {
            `
            .drawerBox {   
              max-width: 182px;  
              padding: 20px;       
              position:fixed;
              z-index: 999;
              background-color: black;
              right: 0;
              top: 300px;
              display:flex;
              flex-direction: column;
            }
            .infoBox {
              padding: 20px;
              display:flex;
              flex-direction: column;
              justify-content: space-between;
              align-items: center;           
              border-bottom: 1px solid #979797;
            }
            .drawerBox .infoBox:nth-child(3) {
              border-bottom: unset;
            }
            .infoBox img{
              margin-bottom: 10px;
              max-width: 64px;
            }
            .infoBox p{
              color: #fff;
              text-align: center;
            }
            .TrapezoidTag {
              position:absolute;
              border-bottom: 120px solid transparent;
              border-left: 100px solid red;
              border-right: 100px solid red;
              border-top: 100px solid red;
              background-color: red;
              height: 0;
              width: 100px;
            }
            `
          }
        </style>
      </div>

    );
  }
}

export default Drawer;
