import React, {Fragment, useState} from 'react';
import MobileLayoutLevel2 from '../../components/layouts/MobileLayoutLevel2';
import css from '../../styles/main.scss';
import {Tab, Tabs, TabList, TabPanel} from 'react-tabs';
import {makeStyles} from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
import OutlineBtn from '../../components/OutlineBtn';
import Modal from 'react-modal';
import Item from '../../components/Item';
import Router from 'next/router';

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

const modalStyles = {
  content: {
    width: '320px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#d8d8d8',
    padding: '0',
    top: '35%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none'
  }
};

Modal.setAppElement('#__next');

const Notice = () => {
  const classes = useStyles();
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.subtitle.style.color = '#fff';
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  const renderAccountItem = data => {
    const filterdArr = data.filter(i => i.value !== '維護中');
    return filterdArr.map(d => <div className={css.accountItem}>{d.name}</div>);
  };
  const renderOptions = data => {
    const filterdArr = data.filter(i => i.value !== '維護中');
    return filterdArr.map(d => <option>{`轉帳到${d.name}`}</option>);
  };

  const _class = {
    true: 'iconfont icon_chevron-up-light',
    false: 'iconfont icon_chevron-down-light'
  };

  const [accordian, setAccordian] = useState(false);
  const showAccordian = () => {
    setAccordian(!accordian);
  };
  const handleSubmit = e => {
    e.preventDefault();
  };
  return (
    <Fragment>
      <MobileLayoutLevel2 title="通知" path={true}>
        <div
          className={css.mobileBox}
          style={{height: '650px', paddingBottom: 80, overflowY: 'scroll'}}
        >
          <Tabs defaultIndex={0} onSelect={index => console.log(index)}>
            <TabList>
              <Tab>個人訊息</Tab>
              <Tab>公告專區</Tab>
            </TabList>
            <TabPanel>
              <div className={classes.root}>
                <Button
                  variant="contained"
                >全選
                </Button>
                <Button variant="contained" color="primary">
                  取消
                </Button>
                <Button
                  variant="contained"
                  color="secondary"
                  className={classes.button}
                  startIcon={<DeleteIcon/>}
                >
                  刪除
                </Button>
                <Button variant="contained" color="primary">
                  線上留言
                </Button>
              </div>
            </TabPanel>
            <TabPanel>
              <div className={css.itemContainer}>
                <div
                  onClick={showAccordian}
                  style={{
                    display: 'flex',
                    justifyContent: 'flex-end',
                    alignItems: 'center',
                    width: '100%',
                    height: 50
                  }}
                >
                  <div
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      position: 'absolute',
                      left: '50%',
                      transform: 'translateX(-50%)'
                    }}
                  >
                    <div style={{fontSize: '1rem', color: 'black'}}>
                      搜尋條件
                    </div>
                  </div>

                  <span
                    className={_class[accordian]}
                    style={{fontSize: '1rem', color: '#9BA2AA'}}
                  />
                </div>
                {accordian && (
                  <div
                    style={{
                      width: '100%',
                      padding: '20px 0',
                      borderTop: '1px solid #979797'
                    }}
                  >
                    <form onSubmit={handleSubmit}>
                      <div class="form-group">
                        <label
                          style={{color: 'black'}}
                          for="exampleFormControlSelect1"
                        >
                          類型
                        </label>
                        <select
                          class="form-control"
                          id="exampleFormControlSelect1"
                        >
                          <option>全部</option>
                          {renderOptions(accountData)}
                        </select>
                      </div>
                      <button
                        style={{width: '100%'}}
                        className="btn btn-primary"
                      >
                        提交
                      </button>
                    </form>
                  </div>
                )}
              </div>
              <Item
                from="主帳戶轉出"
                time="01-16 10:29:34"
                data={{from: '主帳戶', to: '六合彩球', value: 0}}
                result={{status: '成功', amount: 250}}
              />
            </TabPanel>
          </Tabs>
        </div>
      </MobileLayoutLevel2>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="Example Modal"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            請輸入提款密碼
          </div>
          <span className="iconfont icon_times-light" onClick={closeModal}/>
        </div>

        <div className={css.modalBody}>
          <form className={css.formGroup}>
            <div className="inputWithBtn">
              <input type="text" placeholder="6-10位英、數字符"/>
              <button type="button" className="btn btn-primary btn-sm">
                忘記密碼
              </button>
            </div>
            <style jsx>
              {`
                .inputWithBtn {
                  display: flex;
                  margin-top: 20px;
                  margin-bottom: 10px;
                }
                .inputWithBtn .btn {
                  flex: 1 1 40%;
                  margin-left: 10px;
                }
                .btn-submit {
                  background-color: #32c5ff;
                  color: white;
                }
              `}
            </style>
            <button type="button" className="btn btn-submit btn-sm">
              確認送出
            </button>
          </form>
        </div>
      </Modal>
    </Fragment>
  );
};

export default Notice;
