import React, {Fragment, useState} from 'react';
import Modal from "react-modal";

function Wallet(props) {
  const [modalIsOpen, setModalIsOpen] = useState(true);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  return (
    <Fragment>
      <div
        onClick={closeModal}
        className='wallet'>
        <span className="iconfont icon_times-light" style={{fontSize: 26, color: '#32C5FF'}}/>
      </div>
      <div className='walletBox'>
        <div className='walletMain'>
          <div className="mainNav">
            <div className='tab active'>轉帳</div>
            <div className='tab'>存款</div>
            <div className='tab'>提款</div>
            <div className='tab'>歷史</div>
            <div className='tab'>更改密碼</div>
          </div>
          <div className="uiBox">
            <div className="uiContainer">
              <form>
                <div className="form-group row">
                  <label htmlFor="inputPassword" className="col-sm-3 col-form-label" style={{color:'white'}}>從這轉帳</label>
                  <div className="col-sm-9">
                    <select className="form-control form-control-sm">
                      <option>— 請選擇 —</option>
                    </select>
                  </div>
                </div>
                <div className="form-group row">
                  <label htmlFor="inputPassword" className="col-sm-3 col-form-label" style={{color:'white'}}>轉帳去到</label>
                  <div className="col-sm-9">
                    <select className="form-control form-control-sm">
                      <option>— 請選擇 —</option>
                    </select>
                  </div>
                </div>
                <div className="form-group row">
                  <label htmlFor="inputPassword" className="col-sm-3 col-form-label" style={{color:'white'}}>轉帳金額</label>
                  <div className="col-sm-9">
                    <input type="password" className="form-control form-control-sm" id="inputPassword"/>
                  </div>
                </div>
                <button className='submitBtn'>轉帳</button>
              </form>
            </div>
          </div>
        </div>
        <div className='itemBox'>

        </div>
      </div>
      <style jsx>
        {
          `
          .submitBtn {
            background-image: linear-gradient(225deg, #B620E0 0%, #F7B500 100%);
            color: #fff;
            border: none;
            width: 100%;
            padding: 6px;
            border-radius: 19px;
          }
          .uiBox {
            width: 100%;
            height: calc(100% - 60px);
            display:flex;
            justify-content: center;
            align-items: center;
          }
          .uiContainer {  
            flex-grow: 1;          
            max-width: 495px;
            margin: auto;
          }
          .tab {
            color: rgba(255,255,255,.55);
            padding: 10px 20px;
          }
          .tab.active {
            color: rgba(255,255,255,1);
            border-bottom: 3px solid #00ADFF;
          }
          .mainNav {
            max-width: 530px;
            margin: auto;
            height: 60px;
            display:flex;
            justify-content: space-between;
            align-items: center;
          }
          .walletMain {
            margin: 15px 7.5px 15px 15px;
            flex: 1 1 65%;
            max-width: 619px;
            height: 542px;
            background-color: rgba(0,0,0,0.75);
            border-radius: 8px;
          }
          .itemBox {
            margin: 15px 15px 15px 7.5px;
            flex: 1 1 35%;
            max-width: 323px;
            height: 542px;
            background-color: rgba(0,0,0,0.75);
            border-radius: 8px;
          }
          .walletBox {
            flex-grow: 1;
            width: 100%;
            max-width: 994px;
            display:flex;
            justify-content: space-between;
          }
          .wallet {
            position: absolute;
            top: -18px;
            right: -18px;
            box-sizing: border-box;
            border: 6px solid #32C5FF;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: black;
            width: 50px;
            height: 50px;
            border-radius: 50px;
          }
          `
        }
      </style>
    </Fragment>
  );
}

export default Wallet;
