import React from 'react';
import css from "../styles/main.scss";

function BonusRecordItem(props) {
  return (
    <div className={css.itemContainer} style={{padding:0}}>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          width: '100%'
        }}
      >
        <div style={{textAlign:'center',flexBasis:'33.33%',fontSize: '1rem', color: 'black',padding:'15px 0'}}>首儲</div>
        <div style={{textAlign:'center',flexBasis:'33.33%',fontSize: '1rem', color: 'black',padding:'15px',borderRight:'1px solid #ccc',borderLeft:'1px solid #ccc'}}>詳細內容</div>
        <div style={{textAlign:'center',flexBasis:'33.33%',fontSize: '1rem', color: 'black',padding:'15px 0'}}>前往領取</div>
      </div>

    </div>
  );
}

export default BonusRecordItem;
