import React, { Fragment, useState } from 'react';
import MobileLayoutLevel2 from '../../components/layouts/MobileLayoutLevel2';
import css from '../../styles/main.scss';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import OutlineBtn from '../../components/OutlineBtn';
import Modal from 'react-modal';
import Router from 'next/router';

const modalStyles = {
  content: {
    width: '320px',
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#d8d8d8',
    padding: '0',
    top: '35%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    transform: 'translate(-50%, -50%)',
    border: 'none'
  }
};

Modal.setAppElement('#__next');

const PlatformTransfer = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    Modal.subtitle.style.color = '#fff';
    Modal.defaultStyles.overlay.backgroundColor = 'rgba(0,0,0,.65)';
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  return (
    <Fragment>
      <MobileLayoutLevel2 title="平台轉帳">
        <div
          className={css.mobileBox}
          style={{ height: 'auto', paddingBottom: 80 }}
        >
          <Tabs defaultIndex={0} onSelect={index => console.log(index)}>
            <TabList>
              <Tab>平台轉帳</Tab>
              <Tab>轉帳記錄</Tab>
            </TabList>
            <TabPanel>
              <form>
                <div className="form-group">
                  <label>轉帳金額</label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="請輸入整數金額"
                  />
                </div>

                <div
                  style={{ display: 'flex', justifyContent: 'space-between' }}
                >
                  <div
                    style={{
                      flex: '1 1 56%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between'
                    }}
                    className="btn btn-primary"
                  >
                    全部轉回主帳戶
                    <span
                      style={{ lineHeight: '1rem' }}
                      className="iconfont icon_redo-alt-regular"
                    />
                  </div>

                  <OutlineBtn
                    style={{ flex: '1 1 44%', padding: '5px 15px' }}
                    name="確認送出"
                  />
                </div>
              </form>
            </TabPanel>
            <TabPanel>
              <form>
                <div className="form-group">
                  <label>手機號碼</label>
                  <input
                    type="number"
                    className="form-control"
                    placeholder="6-10位英、數字符"
                  />
                </div>
                <div className="form-group">
                  <label>電子信箱</label>
                  <input
                    type="mail"
                    className="form-control"
                    placeholder="6-10位英、數字符"
                  />
                </div>
                <div className="form-row" style={{ marginBottom: 20 }}>
                  <div className="col">
                    <label>電子信箱</label>
                    <input type="text" className="form-control" />
                  </div>
                  <div className="col">
                    <label>電子信箱</label>
                    <input type="text" className="form-control" />
                  </div>
                </div>
                <label>收貨地址</label>
                <div className="form-row" style={{ marginBottom: 20 }}>
                  <div className="form-group col-6">
                    <select
                      id="inputState"
                      className="form-control"
                      defaultValue="請選擇城市"
                    >
                      <option>...</option>
                      <option>...</option>
                    </select>
                  </div>

                  <div className="form-group col-6">
                    <select
                      id="inputState"
                      className="form-control"
                      defaultValue="鄉鎮市區"
                    >
                      <option>...</option>
                      <option>...</option>
                    </select>
                  </div>
                  <div className="form-group col">
                    <input type="text" className="form-control" />
                  </div>
                </div>

                <OutlineBtn name="確認送出" />
              </form>
            </TabPanel>
          </Tabs>
        </div>
      </MobileLayoutLevel2>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="Example Modal"
      >
        <div className={css.modalHeader}>
          <div ref={subtitle => (Modal.subtitle = subtitle)}>
            請輸入提款密碼
          </div>
          <span className="iconfont icon_times-light" onClick={closeModal} />
        </div>

        <div className={css.modalBody}>
          <form className={css.formGroup}>
            <div className="inputWithBtn">
              <input type="text" placeholder="6-10位英、數字符" />
              <button type="button" className="btn btn-primary btn-sm">
                忘記密碼
              </button>
            </div>
            <style jsx>
              {`
                .inputWithBtn {
                  display: flex;
                  margin-top: 20px;
                  margin-bottom: 10px;
                }
                .inputWithBtn .btn {
                  flex: 1 1 40%;
                  margin-left: 10px;
                }
                .btn-submit {
                  background-color: #32c5ff;
                  color: white;
                }
              `}
            </style>
            <button type="button" className="btn btn-submit btn-sm">
              確認送出
            </button>
          </form>
        </div>
      </Modal>
    </Fragment>
  );
};

export default PlatformTransfer;
