import React, { Component, Fragment } from 'react';
import withSizes from 'react-sizes';
import axios from 'axios';
import DeskTopLayout from '../components/layouts/deskTopLayout';
import StartPlayingDesktop from '../components/StartPlayingDesktop';
import UserEvaluationSec from '../components/UserEvaluationSec';
import Bonus from '../components/Bonus';
import AboutSec from '../components/AboutSec';
import Footer from '../components/Footer';
import Slider from 'react-slick';
import SectionTitle from '../components/includes/SectionTitle';
import css from '../styles/main.scss';
import Link from 'next/link';

class Index extends Component {
  static async getInitialProps({ pathname, query, asPath, req, res }) {
    let userData;

    try {
      const response = await axios.get(
        'https://jsonplaceholder.typicode.com/users'
      );
      userData = response.data;
    } catch (e) {
      console.log('error');
    }

    // console.log(pathname);
    // console.log(query);
    // console.log(req);
    return {
      user: {
        name: 'John',
        lastname: 'Do'
      },
      userData
    };
  }

  constructor(props) {
    super(props);
    // console.log(props);

    this.state = {
      users: props.userData,

    };
  }

  render() {
    const settings = {
      autoplay: true,
      dots: true,
      infinite: true,
      arrows: true,
      fade: true,
      speed: 3000,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    const settings_bonus = {
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 3,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    const { bonus } = this.state;

    return (
      <Fragment>
        <DeskTopLayout>
          <Slider {...settings}>
            <img
              src="images/banner/banner01.jpeg"
              alt=""
              className="img-responsive"
            />
            <img
              src="images/banner/banner02.jpg"
              alt=""
              className="img-responsive"
            />
            <img
              src="images/banner/banner03.jpg"
              alt=""
              className="img-responsive"
            />
            <img
              src="images/banner/banner04.jpg"
              alt=""
              className="img-responsive"
            />
          </Slider>
          <div className="container">
            <SectionTitle subject="開始遊玩" />

            <StartPlayingDesktop />

            <SectionTitle subject="優惠活動" />

            <Slider {...settings_bonus}>
              {bonus.map((b, i) => (
                <Bonus key={i} />
              ))}
            </Slider>
          </div>
          <UserEvaluationSec />
          <AboutSec />
          <Footer />
        </DeskTopLayout>
      </Fragment>
    );
  }
}

const mapSizesToProps = ({ width }) => ({
  isMobile: width < 768
});
export default withSizes(mapSizesToProps)(Index);
