import React, { Component, Fragment } from 'react';
import Link from 'next/link';
import DropDownMenu from '../DDM';
import css from '../../styles/main.scss';
import withSizes from 'react-sizes';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';

const useStyles = makeStyles({
  list: {
    width: 350,
    backgroundImage:
      'linear-gradient(0deg, #402F80 0%, #6E2DA1 43%, #1E1549 100%)',
    color: 'white',
    height: '100%'
  },
  fullList: {
    width: 'auto'
  }
});

const MainNav = ({ isMobile, isTablet }) => {
  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: true,
    bottom: false,
    right: false
  });

  const toggleDrawer = (side, open) => event => {
    if (
      event.type === 'keydown' &&
      (event.key === 'Tab' || event.key === 'Shift')
    ) {
      return;
    }

    setState({ ...state, [side]: open });
  };

  const sideList = side => (
    <div
      className={`${classes.list} ${css.slideMenu}`}
      role="presentation"
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
    >
      <div className={css.home}>
        <span
          className="iconfont icon_home-solid
"
        />
      </div>
      <div className={css.menuBlock}>
        <div className={css.menuMain}>

        </div>
      </div>
    </div>
  );

  return (
    <Fragment>
      <header>
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-light">
            {isTablet ? (
              <span
                onClick={toggleDrawer('left', true)}
                className="iconfont icon_bars-light"
              />
            ) : (
              ''
            )}
            <Drawer open={state.left} onClose={toggleDrawer('left', false)}>
              {sideList('left')}
            </Drawer>
            <Link href="/">
              <a className={`navbar-brand ${css.logo}`}>
                <img
                  className="img-responsive logo"
                  src="images/Cali_logo_02.png"
                  alt=""
                />
              </a>
            </Link>
            {isTablet ? (
              ''
            ) : (
              <div className="collapse navbar-collapse">
                <div className={css.flexContainer}>
                  <Link href="/online_baccarat">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      真人娛樂
                    </a>
                  </Link>

                  <Link href="/sports">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      體育博彩
                    </a>
                  </Link>
                  <Link href="/lottery">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      賓果彩票
                    </a>
                  </Link>
                  <Link href="/electronics">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      電子遊戲
                    </a>
                  </Link>
                  <Link href="/fish">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      捕魚王
                    </a>
                  </Link>
                  <Link href="/promotions">
                    <a
                      className="nav-item nav-link"
                      style={{ padding: '.5rem 0' }}
                    >
                      優惠活動
                    </a>
                  </Link>
                  <DropDownMenu />
                </div>
              </div>
            )}
          </nav>
        </div>
        <style jsx>
          {`
            span.iconfont {
              margin-left: 10px;
              font-size: 1.875rem;
              background: linear-gradient(
                180deg,
                #fdf8a2 3%,
                #f4d97a 47%,
                #eec462 100%
              );
              -webkit-background-clip: text;
              -webkit-text-fill-color: transparent;
            }
          `}
        </style>
      </header>
    </Fragment>
  );
};

const mapSizesToProps = ({ width }) => ({
  isMobile: width < 480,
  isTablet: width >= 480 && width <= 1024
});
export default withSizes(mapSizesToProps)(MainNav);
