import React from 'react';
import css from '../styles/main.scss';
import Router from 'next/router';

const OutlineBtn = props => {
  const handleClick = ({ path, func, back }) => {
    if (path) {
      return () => Router.push(path);
    } else if (func) {
      return func;
    } else if (back) {
      return () => Router.back();
      console.log('');
    }
  };
  return (
    <div onClick={handleClick} className={css.outlineBtn}>
      {props.name}
    </div>
  );
};

export default OutlineBtn;
