          title={banner.title}
          desc={banner.desc}
          bannerIMG={banner.pic}
        />
        <div className={css.bonusBox}></div>
      </div>

      <Footer />
    </DeskTopLayout>
    {isMobile && <MobileLayout></MobileLayout>}
  </Fragment>
);

